import React, { Component } from 'react';
import Flippy, { FrontSide, BackSide } from './../lib';
import Button from 'react-bootstrap/Button';
import { MDBRow, MDBCol, MDBIcon, MDBBtn } from "mdbreact";
import './style.css';

function Flashcard(props) {

const FlippyStyle = {
  width: '200px',
  height: '200px',
  textAlign: 'center',
  color: '#FFF',
  fontFamily: 'sans-serif',
  fontSize: '30px',
  justifyContent: 'center'
}

const DefaultCardContents = ({ children }) => (
  <React.Fragment>
    <FrontSide
      style={{
        backgroundColor: 'white',
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column'
      }}
    >
      <img
        src="https://parent.guide/wp-content/uploads/2014/08/Banana-baby-food-recipes.jpg"
        style={{ maxWidth: '100%', maxHeight: '100%' }}
      />
      <span
        style={{
          fontSize: '12px',
          position: 'absolute',
          bottom: '10px',
          width: '100%'
        }}>
        {children}<br />
        Hover over to show key word
      </span>
    </FrontSide>
    <BackSide
      style={{
        backgroundColor: '#EB6864',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        flexDirection: 'column'
      }}>
      <h1>Banana</h1>
      <span
        style={{
          fontSize: '12px',
          position: 'absolute',
          bottom: '10px',
          width: '100%'
        }}>
        {children}<br />
        <Button variant="success">Success</Button>
        <Button variant="outline-warning">Warning</Button>
      </span>
    </BackSide>
  </React.Fragment>);

const FlippyOnHover = ({ flipDirection = 'vertical' }) => (
  <Flippy
    flipOnHover={true}
    flipDirection={flipDirection}
    style={FlippyStyle}
  >
    <DefaultCardContents>
    </DefaultCardContents>
  </Flippy>
);

class Flashcard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isFlipped: false
    }
  }

  render() {
    return (
      <div className="App">
        <div style={{ display: 'flex', flex: '1 0 200px', justifyContent: 'space-around', 'flex-wrap': 'wrap' }}>
          <FlippyOnHover flipDirection="horizontal" />
        </div>
      </div>
    );
  }
}}

export default Flashcard;
