import React, { Component } from "react";
import DeleteBtn from "../components/DeleteBtn";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, TextArea, FormBtn } from "../components/Form";

class Units extends Component {
    state = {
        units: [],
        name: "",
        section1: "",
        section2: "",
        section3: ""
    };

    componentDidMount() {
        this.loadUnits();
    }

    loadUnits = () => {
        API.getUnits()
            .then(res =>
                // this.setState({ units: res.data, name: "", section1: "", section2: "", section3: "" })
            console.log(res)
                )
            .catch(err => console.log(err));
    };

    deleteUnit = id => {
        API.deleteUnit(id)
            .then(res => this.loadUnits())
            .catch(err => console.log(err));
    };

    handleInputChange = event => {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        });
    };

    handleFormSubmit = event => {
        event.preventDefault();
        if (this.state.name && this.state.section1) {
            API.saveUnit({
                name: this.state.name,
                section1: this.state.section1,
                section1: this.state.section2,
                section1: this.state.section3
            })
                .then(res => this.loadUnits())
                .catch(err => console.log(err));
        }
    };

    render() {
        return (
            <Container fluid>
                <Row>
                    <Col size="md-3">
                        <form>
                            <Input
                                value={this.state.name}
                                onChange={this.handleInputChange}
                                name="name"
                                placeholder="Unit Name (required)"
                            />
                            <Input
                                value={this.state.section1}
                                onChange={this.handleInputChange}
                                name="section1"
                                placeholder="Section 1 (required)"
                            />
                            <TextArea
                                value={this.state.section2}
                                onChange={this.handleInputChange}
                                name="section2"
                                placeholder="Section 2 (optional)"
                            />
                            <TextArea
                                value={this.state.section3}
                                onChange={this.handleInputChange}
                                name="section3"
                                placeholder="Section  (optional)"
                            />
                            <FormBtn
                                disabled={!(this.state.name && this.state.section1)}
                                onClick={this.handleFormSubmit}
                            >
                                Add Unit
              </FormBtn>
                        </form>
                    </Col>
                    <Col size="md-9">
                        {this.state.units.length ? (
                            <List>
                                {this.state.units.map(unit => (
                                    <ListItem key={unit._id}>
                                        <Link to={"/units/" + unit._id}>
                                            <strong>
                                                {unit.name}
                                            </strong>
                                        </Link>
                                        <DeleteBtn onClick={() => this.deleteUnit(unit._id)} />
                                    </ListItem>
                                ))}
                            </List>
                        ) : (
                                <h3>No Units to Display</h3>
                            )}
                    </Col>
                </Row>
            </Container>
        );
    }
}

export default Units;
