import React, { Component } from "react";
import Flashcard from "../components/Flashcard";
import API from "../utils/API";
import { List, ListItem } from "../components/List";
import DeleteBtn from "../components/DeleteBtn";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { Input, TextArea, FormBtn } from "../components/Form";
import AddUnitModal from "../components/AddUnitModal";

class Flashcards extends Component {

  state = {
    flashcards: [],
    flashcardName: "",
    flashcardImage: ""
  };

  componentDidMount() {
    this.loadFlashcards();
  };

  loadFlashcards = () => {
    API.getFlashcards()
      .then(res => {
        // console.log(res.data);
        this.setState({ flashcards: res.data, flashcardName: "", flashcardImage: "" })
        console.log("flashhhhhhhhhh" + JSON.stringify(this.state.flashcards));
      })
      .catch(err => console.log(err));
  };

  render() {
    return (
      // <div>
      //   <h1>My flashcards</h1>
      //   {this.state.flashcards.length? (
      //   <List>
      //     {this.state.flashcards.map(flashcard => {
      //       console.log(flashcard);
            
      //       // <Flashcard
      //       //   key={flashcard._id}
      //       //   flashcardWord={flashcard.flashcardName}
      //       //   flashcardImage={flashcard.flashcardImage} >
      //       // </Flashcard>
      //     })}
      //   </List> ) : (
      //     <h3>No results to display</h3>
      //   )}


      // </div>
<Container fluid>
<Row>
  <Col size="md-3 sm-12">                
    {this.state.flashcards.length ? (
      <List>
        {this.state.flashcards.map(flashcards => (
          <ListItem key={flashcards._id}>
          <h1>{flashcards.flashcardName}</h1>
          </ListItem>
        ))}
      </List>
    ) : (
        <h3>No Results to Display</h3>
      )}
     </Col>
  <Col size="md-9">       
  </Col>          
</Row>
</Container>


    );
  }
}

export default Flashcards;
