import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";

class Detail extends Component {
  state = {
    unit: {}
  };
  // When this component mounts, grab the book with the _id of this.props.match.params.id
  // e.g. localhost:3000/books/599dcb67f0f16317844583fc
  componentDidMount() {
    console.log(this)
    API.getUnit(this.props.match.params.id)
      .then(res => this.setState({ unit: res.data }))
      .catch(err => console.log(err));
  }

  render() {
    return (
      <Container fluid>
        <Row>
          <Col size="md-12">
            <Jumbotron>
              <h1>
                {this.state.unit.title}
              </h1>
            </Jumbotron>
          </Col>
        </Row>
        <Row>
          <Col size="md-10 md-offset-1">
            <article>
              <h1>Section 1</h1>
              <p>
                {this.state.unit.section1}
              </p>
            </article>
            <article>
              <h1>Section 2</h1>
              <p>
                {this.state.unit.section2}
              </p>
            </article>
            <article>
              <h1>Section 3</h1>
              <p>
                {this.state.unit.section3}
              </p>
            </article>
          </Col>
        </Row>
        <Row>
          <Col size="md-2">

 <AddUnitModal>

               <form>
              <Input
                value={this.state.title}
                onChange={this.handleInputChange}
                name="title"
                placeholder="Title (required)"
              />
              <Input
                value={this.state.section1}
                onChange={this.handleInputChange}
                name="section1"
                placeholder="Section One (required)"
              />
              <TextArea
                value={this.state.section2}
                onChange={this.handleInputChange}
                name="section2"
                placeholder="Section Two (Optional)"
              />
              <TextArea
                value={this.state.section3}
                onChange={this.handleInputChange}
                name="section3"
                placeholder="Section Three (Optional)"
              />
              <FormBtn
                disabled={!(this.state.title && this.state.section1)}
                onClick={this.handleFormSubmit}
              >
                Add Unit
              </FormBtn>
            </form>
             </AddUnitModal>



            <Link to="/">‚Üê Back</Link>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Detail;
