import React, { Component } from "react";
import DeleteBtn from "../components/DeleteBtn";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, TextArea, FormBtn } from "../components/Form";

class Units extends Component {
  state = {
    units: [],
    title: "",
    section1: "",
    section2: "",
    section3: ""
  };

  componentDidMount() {
    this.loadUnits();
  }

  loadUnits = () => {
    API.getUnits()
      .then(res =>
        this.setState({ units: res.data,  section1: "", section2: "", section3: "" })
      )
      .catch(err => console.log(err));
  };

  deleteUnit = id => {
    API.deleteUnit(id)
      .then(res => this.loadUnit())
      .catch(err => console.log(err));
  };

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    if (this.state.title && this.state.section1) {
      API.saveUnit({
        title: this.state.title,
        section1: this.state.section1,
        section2: this.state.section2,
        section3: this.state.section3
      })
        .then(res => this.loadUnits())
        .catch(err => console.log(err));
    }
  };

  render() {
    return (
      <Container fluid>
        <Row>
          <Col size="md-6">
            <form>
              <Input
                value={this.state.title}
                onChange={this.handleInputChange}
                name="title"
                placeholder="Title (required)"
              />
              <Input
                value={this.state.section1}
                onChange={this.handleInputChange}
                name="section1"
                placeholder="Author (required)"
              />
              <TextArea
                value={this.state.section2}
                onChange={this.handleInputChange}
                name="section2"
                placeholder="Synopsis (Optional)"
              />
                     <TextArea
                value={this.state.section3}
                onChange={this.handleInputChange}
                name="section2"
                placeholder="Synopsis (Optional)"
              />
              <FormBtn
                disabled={!(this.state.author1 && this.state.title1)}
                onClick={this.handleFormSubmit}
              >
                Submit Book1
              </FormBtn>
            </form>
          </Col>
          <Col size="md-6 sm-12">
            {this.state.books1.length ? (
              <List>
                {this.state.books1.map(book1 => (
                  <ListItem key={book1._id}>
                    <Link to={"/books1/" + book1._id}>
                      <strong>
                        {book1.title1} by {book1.author1}
                      </strong>
                    </Link>
                    <DeleteBtn onClick={() => this.deleteBook1(book1._id)} />
                  </ListItem>
                ))}
              </List>
            ) : (
                <h3>No Results to Display</h3>
              )}
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Books1;
