import React, { Component } from "react";
import Flashcard from "../components/Flashcard";
import API from "../utils/API";
import { List, ListItem } from "../components/List";
import DeleteBtn from "../components/DeleteBtn";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { Input, TextArea, FormBtn } from "../components/Form";
import AddUnitModal from "../components/AddUnitModal";
import Card from 'react-bootstrap/Card'
import CardColumns from 'react-bootstrap/CardColumns'

class Flashcards extends Component {

  state = {
    flashcards: [],
    flashcardName: "",
    flashcardImage: ""
  };

  componentDidMount() {
    this.loadFlashcards();
  };

  loadFlashcards = () => {
    API.getFlashcards()
      .then(res => {
        // console.log(res.data);
        this.setState({ flashcards: res.data, flashcardName: "", flashcardImage: "" })
        console.log("flashhhhhhhhhh" + JSON.stringify(this.state.flashcards));
      })
      .catch(err => console.log(err));
  };

  render() {
    return (

      <CardColumns >
       
        {this.state.flashcards.length ? (
          <div>
            {this.state.flashcards.map(flashcards => {
              // <div key={flashcards._id}>
                {/* <Card.Img src={flashcards.flashcardImage} alt="Card image" />
                <Card.ImgOverlay>
                  <Card.Title>HELLLLLLLOOOOO{flashcards.flashcardName}</Card.Title>
                </Card.ImgOverlay> */}
              // </div>
              console.log(flashcards)







              })}
          </div>
        ) : (
            <h3>No Results to Display</h3>
          )}
      </CardColumns>


    );
  }
}

export default Flashcards;
