import React, { Component } from 'react';
import Flippy, { FrontSide, BackSide } from './../lib';
import Button from 'react-bootstrap/Button';
import { MDBRow, MDBCol, MDBIcon, MDBBtn } from "mdbreact";


import './style.css';

const FlippyStyle = {
  width: '200px',
  height: '300px',
  textAlign: 'center',
  color: '#FFF',
  fontFamily: 'sans-serif',
  fontSize: '30px',
  justifyContent: 'center'
}

loadUnits = () => {
  API.getFLashcard()
    .then(res =>
      this.setState({ units: res.data, section1: "", section2: "", section3: "" })
    )
    .catch(err => console.log(err));
};


const DefaultCardContents = ({ children }) => (
  <React.Fragment>
    <FrontSide
      style={{
        backgroundColor: 'white',
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column'
      }}
    >
      <img
        src="https://parent.guide/wp-content/uploads/2014/08/Banana-baby-food-recipes.jpg"
        style={{ maxWidth: '100%', maxHeight: '100%' }}
      />
      <span 
        style={{
          fontSize:'12px',
          position: 'absolute',
          bottom: '10px',
          width: '100%'
        }}>
        {children}<br />
       Hover over to show key word
      </span>
    </FrontSide>
    <BackSide
      style={{
        backgroundColor: '#EB6864',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        flexDirection: 'column'
      }}>
       <h1>Banana</h1>
      <span 
        style={{
          fontSize:'12px',
          position: 'absolute',
          bottom: '10px',
          width: '100%'
        }}>
        {children}<br />
        <MDBBtn floating size="lg" gradient="purple"><MDBIcon icon="bolt" /></MDBBtn>
        <Button variant="success">Success</Button>
        <Button variant="outline-warning">Warning</Button>
      </span>
    </BackSide>
  </React.Fragment>);

const FlippyOnHover = ({ flipDirection = 'vertical' }) => (
  <Flippy
    flipOnHover={true}
    flipDirection={flipDirection}
    style={FlippyStyle}
  >
    <DefaultCardContents>
    </DefaultCardContents>
  </Flippy>
);

const FlippyOnClick = ({ flipDirection = 'vertical' }) => (
  <Flippy
    flipOnClick={true}
    flipDirection={flipDirection}
    style={FlippyStyle}
  >
    <DefaultCardContents>
    </DefaultCardContents>
  </Flippy>
);

const ControlledFlippy = ({ isFlipped })  => (
  <Flippy
    flipDirection="vertical"
    isFlipped={isFlipped}
    style={FlippyStyle}
  >
    <DefaultCardContents>
      I flip vertically for every 3sec. I am controlling by a upper scope.
    </DefaultCardContents>
  </Flippy>
);

class Flashcard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isFlipped: false
    };

    setInterval(() => {
      this.setState({
        isFlipped: !this.state.isFlipped
      });
    }, 3000);
  }

  render() {
    return (
      <div className="App">
        <div style={{ display: 'flex', flex: '1 0 200px', justifyContent: 'space-around', 'flex-wrap': 'wrap' }}>
            {/* <Flippy
              ref={(r) => this.flippyHorizontal = r}
              flipOnClick={false}
              style={FlippyStyle}
            >
            <DefaultCardContents>
              I flip horizontally with an event<br />
              <button type="button" onClick={() => this.flippyHorizontal.toggle()}>Toggle Me!</button>
            </DefaultCardContents>
          </Flippy> */}
          <FlippyOnHover flipDirection="horizontal"/>
          {/* <FlippyOnClick flipDirection="horizontal"/>
          <Flippy
            ref={(r) => this.flippyVertical = r}
            flipOnClick={false}
            flipDirection="vertical"
            style={FlippyStyle}
          >
            <DefaultCardContents>
              I flip vertically with an event<br />
              <button type="button" onClick={() => this.flippyVertical.toggle()}>Toggle Me!</button>
            </DefaultCardContents>
          </Flippy>
          <FlippyOnHover />
          <FlippyOnClick />
          <ControlledFlippy
            isFlipped={this.state.isFlipped}
          />  */}
        </div>
      </div>
    );
  }
}

export default Flashcard;
